CREATE TABLE IF NOT EXISTS runs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER,
    miles INTEGER,
    start_time TIMESTAMP,
    end_time TIMESTAMP,
    start_location TEXT,
    end_location TEXT,
    weather_id INTEGER,
    is_legit BOOLEAN,
    FOREIGN KEY (user_id) REFERENCES users(id)
);


SELECT * FROM users;
SELECT * FROM runs;
SELECT
    first_name || ' ' || last_name AS Name,
    miles,
    start_time,
    end_time,
    printf('%02d:%02d:%02d',
        (strftime('%s', end_time) - strftime('%s', start_time)) / 3600,
        ((strftime('%s', end_time) - strftime('%s', start_time)) % 3600) / 60,
        (strftime('%s', end_time) - strftime('%s', start_time)) % 60
    ) AS time
FROM runs
LEFT JOIN users ON runs.user_id = users.id;



CREATE TABLE IF NOT EXISTS runnings (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER NOT NULL,
    route_coords TEXT NOT NULL,
    miles REAL NOT NULL,
    time TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(id)
);

